mapper.select:
select

from saas_borrower_identity_info
where borrower_code = #{borrowerCode,jdbcType=VARCHAR}

    and order_numb = #{orderNumb,jdbcType=VARCHAR}


    and order_numb is null

and deleted = 0

select COUNT(1)
from saas_borrower_identity_info
where borrower_code = #{borrowerCode,jdbcType=VARCHAR}

    and order_numb = #{orderNumb,jdbcType=VARCHAR}


    and order_numb is null

and deleted = 0
