mapper.update:
update  saas_order_bill_detail
set deleted = 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
select

from saas_order_bill_detail


        and id = #{id,jdbcType=BIGINT}


        and order_numb = #{orderNumb,jdbcType=VARCHAR}


        and merchant_code = #{merchantCode,jdbcType=VARCHAR}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and borrower_code = #{borrowerCode,jdbcType=VARCHAR}


        and real_capital = #{realCapital,jdbcType=DECIMAL}


        and repayment_dt = #{repaymentDt,jdbcType=DATE}


        and interest = #{interest,jdbcType=DECIMAL}


        and amount = #{amount,jdbcType=DECIMAL}


        and last_calculate_interest_dt = #{lastCalculateInterestDt,jdbcType=DATE}


        and current_interest_ratio = #{currentInterestRatio,jdbcType=DECIMAL}


        and is_extend = #{isExtend,jdbcType=BIT}


        and paid = #{paid,jdbcType=BIT}


        and paid_amount = #{paidAmount,jdbcType=DECIMAL}


        and deduct_amount = #{deductAmount,jdbcType=DECIMAL}


        and actual_repayment_date = #{actualRepaymentDate,jdbcType=TIMESTAMP}


        and actual_repayment_dt = #{actualRepaymentDt,jdbcType=DATE}


        and created_dt = #{createdDt,jdbcType=DATE}


        and deleted = #{deleted,jdbcType=BIT}


        and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}

select count(*) from saas_order_bill_detail


        and id = #{id,jdbcType=BIGINT}


        and order_numb = #{orderNumb,jdbcType=VARCHAR}


        and merchant_code = #{merchantCode,jdbcType=VARCHAR}


        and channel_code = #{channelCode,jdbcType=VARCHAR}


        and borrower_code = #{borrowerCode,jdbcType=VARCHAR}


        and real_capital = #{realCapital,jdbcType=DECIMAL}


        and repayment_dt = #{repaymentDt,jdbcType=DATE}


        and interest = #{interest,jdbcType=DECIMAL}


        and amount = #{amount,jdbcType=DECIMAL}


        and last_calculate_interest_dt = #{lastCalculateInterestDt,jdbcType=DATE}


        and current_interest_ratio = #{currentInterestRatio,jdbcType=DECIMAL}


        and is_extend = #{isExtend,jdbcType=BIT}


        and paid = #{paid,jdbcType=BIT}


        and paid_amount = #{paidAmount,jdbcType=DECIMAL}


        and deduct_amount = #{deductAmount,jdbcType=DECIMAL}


        and actual_repayment_date = #{actualRepaymentDate,jdbcType=TIMESTAMP}


        and actual_repayment_dt = #{actualRepaymentDt,jdbcType=DATE}


        and created_dt = #{createdDt,jdbcType=DATE}


        and deleted = #{deleted,jdbcType=BIT}


        and gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


        and gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}
