mapper.select:
SELECT order_id
FROM lender_reject_order
WHERE user_id = #{userId,jdbcType=BIGINT}

    AND gmt_create > #{startDate,jdbcType=VARCHAR}

AND deleted=0

SELECT

FROM lender_reject_order
WHERE user_id = #{userId,jdbcType=BIGINT}
AND order_id = #{orderId,jdbcType=BIGINT}
AND deleted = 0
ORDER BY id DESC limit 1
