mapper.select:
SELECT SUM(`value`)  FROM `user_balance_info`


        and  gmt_modified <= #{gmtModified,jdbcType=TIMESTAMP}


        and gmt_modified >= #{startDate,jdbcType=TIMESTAMP}


        and gmt_modified <= #{endDate,jdbcType=TIMESTAMP}

    and  deleted =0

mapper.update:
update  user_balance_info
set deleted = 1
where user_id = #{userId,jdbcType=BIGINT}
