mapper.select:
select

from user_bank_order
where user_id = #{userId,jdbcType=BIGINT}

    and op_type = #{opType,jdbcType=INTEGER}


    and status = #{status,jdbcType=TINYINT}

and deleted=0

SELECT SUM(`amount`)  FROM `user_bank_order`


        AND  op_type = #{opType,jdbcType=TINYINT}


        AND  status = #{status,jdbcType=TINYINT}


        and  gmt_create >= #{gmt_create,jdbcType=TIMESTAMP}


        and gmt_create >= #{startDate,jdbcType=TIMESTAMP}


        and gmt_create <= #{endDate,jdbcType=TIMESTAMP}

    and  deleted =0
