mapper.select:
SELECT count(*) FROM
(
SELECT
id,user_id,target_id,`value`,fee,user_comment AS `comment`,gmt_create
FROM user_transfer_order
WHERE user_id = #{userId,jdbcType=BIGINT}
AND target_id = #{targetId,jdbcType=BIGINT}
AND `status` = 1 AND deleted = 0
UNION ALL
SELECT
id,user_id,target_id,`value`,fee,target_comment AS `comment`,gmt_create
FROM user_transfer_order
WHERE user_id = #{targetId,jdbcType=BIGINT}
AND target_id = #{userId,jdbcType=BIGINT}
AND `status` = 1 AND deleted = 0
) t

SELECT * FROM
(
SELECT
id,user_id,target_id,`value`,fee,user_comment AS `comment`,gmt_create
FROM user_transfer_order
WHERE user_id = #{userId,jdbcType=BIGINT}
AND target_id = #{targetId,jdbcType=BIGINT}
AND `status` = 1 AND deleted = 0
UNION ALL
SELECT
id,user_id,target_id,`value`,fee,target_comment AS `comment`,gmt_create
FROM user_transfer_order
WHERE user_id = #{targetId,jdbcType=BIGINT}
AND target_id = #{userId,jdbcType=BIGINT}
AND `status` = 1 AND deleted = 0
) t ORDER BY t.id DESC
